class Button(pygame.sprite.Sprite):
    text_fit_rect = False
    text_background_color = (255, 255, 255)
    text_alpha = 0
    button_down = False
    selected = False

    def set_image(self, image_normal=IMAGE_NORMAL, image_hover=IMAGE_HOVER, image_down=IMAGE_DOWN):
        self.image_normal_o = pygame.transform.scale(image_normal, (self.rect.width, self.rect.height))
        self.image_hover_o = pygame.transform.scale(image_hover, (self.rect.width, self.rect.height))
        self.image_down_o = pygame.transform.scale(image_down, (self.rect.width, self.rect.height))

        self.image_normal, self.image_hover, self.image_down = self.image_normal_o.copy(), self.image_hover_o.copy(), self.image_down_o.copy()

        self.image = self.image_normal

        self.move(self.rect.left, self.rect.top)

    def set_text(self, text, set_active_image=False, fit_rect=False, background_color=(255, 255, 255), alpha=0, text_color=None):
        if text_color is not None:
            self.text_color = text_color
        self.text = text
        self.text_fit_rect = fit_rect
        self.text_background_color = background_color
        self.text_alpha = alpha
        self.move(self.rect.left, self.rect.top, set_active_image)

    def set_rect(self, x, y, w, h):
        self.image_normal_o = pygame.transform.scale(self.image_normal_o, (int(w), int(h)))
        self.image_hover_o = pygame.transform.scale(self.image_hover_o, (int(w), int(h)))
        self.image_down_o = pygame.transform.scale(self.image_down_o, (int(w), int(h)))

        self.move(x, y, True)

    def move(self, x, y, set_active_image=False):
        self.rect = self.image.get_rect(topleft=(x, y))
        # original
        self.image_normal, self.image_hover, self.image_down = \
            self.image_normal_o.copy(), self.image_hover_o.copy(), self.image_down_o.copy()
        # Blit the text onto the images.
        if self.text != "":
            font = self.font

            if self.text_fit_rect:
                size = 24
                imwidth = self.image_normal.get_width()
                while True:
                    font = pygame.font.SysFont('calibre', size)
                    if font.size(self.text)[0] < imwidth:
                        break
                    size -= 1
            text_surf = font.render(self.text, True, self.text_color)
            image_center = self.image.get_rect().center
            text_rect = text_surf.get_rect(center=image_center)
            if self.text_alpha > 0:
                s = pygame.Surface(text_rect.size)
                s.set_alpha(self.text_alpha)
                s.fill(self.text_background_color)

            for image in (self.image_normal, self.image_hover, self.image_down):
                if self.text_alpha > 0:
                    image.blit(s, text_rect)
                image.blit(text_surf, text_rect)

        if set_active_image:
            self.image = self.image_normal

    def __init__(self, x, y, width, height, callback=lambda: None,
                 callback_left=lambda: None,
                 font=FONT, text='', text_color=(0, 0, 0),
                 image_normal=IMAGE_NORMAL, image_hover=IMAGE_HOVER,
                 image_down=IMAGE_DOWN, fit_text=False, center=False):
        super().__init__()

        self.text = text
        self.font = font
        self.text_color = text_color

        if fit_text:
            f = font.render(text, True, text_color).get_rect()
            if f.width > width:
                width = f.width
            if f.height > height:
                height = f.height

        # original images
        self.image_normal_o = pygame.transform.scale(image_normal, (width, height))
        self.image_hover_o = pygame.transform.scale(image_hover, (width, height))
        self.image_down_o = pygame.transform.scale(image_down, (width, height))
        # Scale the images to the desired size (doesn't modify the originals).
        self.image_normal, self.image_hover, self.image_down = self.image_normal_o.copy(), self.image_hover_o.copy(), self.image_down_o.copy()

        self.image = self.image_normal  # The currently active image.
        if center:
            self.rect = self.image.get_rect(center=(x, y))
        else:
            self.rect = self.image.get_rect(topleft=(x, y))
        # To center the text rect.
        image_center = self.image.get_rect().center
        text_surf = font.render(text, True, text_color)
        text_rect = text_surf.get_rect(center=image_center)
        # Blit the text onto the images.
        for image in (self.image_normal, self.image_hover, self.image_down):
            image.blit(text_surf, text_rect)

        # This function will be called when the button gets pressed.
        self.callback = callback
        self.callback_left = callback_left

    def get_width(self):
        return self.rect.width

    def get_height(self):
        return self.rect.height

    def handle_event(self, event):
        if event.type == pygame.MOUSEBUTTONDOWN:
            if self.rect.collidepoint(event.pos):
                if event.button == 1:
                    self.image = self.image_down
                    self.button_down = True
                    self.callback()
                elif event.button == 3:
                    self.image = self.image_down
                    self.button_down = True
                    self.callback_left()
        elif event.type == pygame.MOUSEBUTTONUP:
            # If the rect collides with the mouse pos.
            if self.rect.collidepoint(event.pos) and self.button_down:
                self.image = self.image_hover
            self.button_down = False
        elif event.type == pygame.MOUSEMOTION:
            collided = self.rect.collidepoint(event.pos)
            if collided and not self.button_down:
                self.image = self.image_hover
            elif not collided:
                self.image = self.image_normal

    def draw(self, screen):
        if self.selected:
            screen.blit(self.image_down, self.rect)
        else:
            screen.blit(self.image, self.rect)


class TextField:
    COLOR_INACTIVE = (120, 0, 0)
    COLOR_ACTIVE = (255, 0, 0)

    def __init__(self, x, y, w, h, text='', enter=lambda: next, change=lambda: next):
        self.enter = enter
        self.change = change
        self.rect = pygame.Rect(x, y, w, h)
        self.color = TextField.COLOR_INACTIVE
        self.text = text
        self.txt_surface = FONT.render(text, True, self.color)
        self.active = False

    def handle_event(self, event):
        if event.type == pygame.MOUSEBUTTONDOWN:
            if self.rect.collidepoint(event.pos):
                self.active = True
            else:
                self.active = False
            self.color = TextField.COLOR_ACTIVE if self.active else TextField.COLOR_INACTIVE
        if event.type == pygame.KEYDOWN:
            if self.active:
                if pygame.key.get_mods() & pygame.KMOD_CTRL:
                    if event.key == pygame.K_x:
                        self.text = ''
                        self.change()
                    elif event.key == pygame.K_c:
                        pyperclip.copy(self.text)
                    elif event.key == pygame.K_v:
                        self.text = pyperclip.paste()
                        self.change()
                elif event.key == pygame.K_RETURN:
                    self.enter()
                elif event.key == pygame.K_BACKSPACE:
                    self.text = self.text[:-1]
                    self.change()
                else:
                    self.text += event.unicode
                    self.change()
                self.txt_surface = FONT.render(self.text, True, self.color)

    def set_rect(self, x, y, w, h):
        self.rect = pygame.Rect(x, y, w, h)

    def move(self, x, y):
        self.rect = pygame.Rect(x, y, self.rect.width, self.rect.height)

    def get_width(self):
        return self.rect.width

    def get_height(self):
        return self.rect.height

    def draw(self, screen):
        screen.blit(self.txt_surface, (self.rect.x + 5, self.rect.y + 5))
        pygame.draw.rect(screen, self.color, self.rect, 2)


class GuiContainer:
    min_scroll = 10
    scroll_offset = 0
    OBJ_MARGIN = 8
    max_scroll = 0

    def __init__(self, nodes, rect=(0, 0, 100, 100), with_columns=True, horizontal=False, care_size=True):
        self.care_size = care_size
        self.horizontal = horizontal
        self.with_columns = with_columns
        self.max_object_size = (0, 0)
        if len(nodes) > 0:
            self.max_object_size = (max([button.get_width() for button in nodes]),
                                    max([button.get_height() for button in nodes]))
        self.rect = rect
        self.cont = Button(0, 0, 100, 100)
        self.nodes = nodes
        self.rebuild()

    def hits(self, pos):
        return self.rect[0] <= pos[0] <= self.rect[2] and self.rect[1] <= pos[1] <= self.rect[3]

    def get_width(self):
        return self.rect[2] - self.rect[0]

    def get_height(self):
        return self.rect[3] - self.rect[1]

    def move(self, x, y):
        self.set_rect(x, y, x + self.get_width(), y + self.get_height())

    def set_rect(self, x, y, w, h):
        self.rect = (x, y, w, h)
        self.rebuild()

    def rebuild(self):
        if self.horizontal:
            if self.with_columns:
                cols = max(1, int((self.get_height() - int(self.OBJ_MARGIN / 2))
                                  / (self.max_object_size[0] + self.OBJ_MARGIN)))
            else:
                cols = 1

            self.cont.set_rect(self.rect[0], self.rect[1], self.get_width(), self.get_height())

            self.max_scroll = (int(len(self.nodes) / cols) + 1) * (
                    self.max_object_size[0] + self.OBJ_MARGIN) - (self.get_width())

            i = 0
            x = self.OBJ_MARGIN
            while i < len(self.nodes):
                self.nodes[i].move(
                    self.rect[0] + self.scroll_offset + x,
                    self.rect[1] + self.OBJ_MARGIN)
                x += self.nodes[i].get_width() + self.OBJ_MARGIN
                i += 1

        else:
            if self.with_columns:
                cols = max(1, int((self.get_width() - int(self.OBJ_MARGIN / 2))
                                  / (self.max_object_size[1] + self.OBJ_MARGIN)))
            else:
                cols = 1

            self.cont.set_rect(self.rect[0], self.rect[1], self.get_width(), self.get_height())

            self.max_scroll = (int(len(self.nodes) / cols) + 1) * (
                    self.max_object_size[1] + self.OBJ_MARGIN) - (self.get_height())

            for i in range(len(self.nodes)):
                self.nodes[i].move(
                    self.rect[0] + self.OBJ_MARGIN + ((self.max_object_size[0] + self.OBJ_MARGIN) * (i % cols)),
                    self.rect[1] + self.scroll_offset + int(i / cols) * (self.max_object_size[1] + self.OBJ_MARGIN))

    def reset_scroll(self):
        self.scroll_offset = self.min_scroll
        self.rebuild()

    def handle_scroll(self, event):
        if not self.hits(event.pos):
            return False

        if event.button == 4:
            if self.scroll_offset < self.min_scroll:
                self.scroll_offset += 10
                self.rebuild()
        elif event.button == 5:
            if self.scroll_offset > -self.max_scroll:
                self.scroll_offset -= 10
                self.rebuild()
        return True

    def handle_event(self, event):
        try:
            if (not self.care_size) or self.hits(event.pos):
                for obj in self.nodes:
                    obj.handle_event(event)
            else:
                for obj in self.nodes:
                    obj.handle_event(EMPTY_MOUSE_EVENT)
        except AttributeError:
            pass

    def draw(self, screen):
        if self.care_size:
            empty_screen = pygame.Surface(screen.get_size())
            self.cont.draw(empty_screen)
            for obj in self.nodes:
                obj.draw(empty_screen)

            screen.blit(empty_screen, self.rect, area=self.rect)
        else:
            self.cont.draw(screen)
            for obj in self.nodes:
                obj.draw(screen)


class ObjectGuiContainer(GuiContainer):
    def deselect_all(self):
        for obj in self.nodes:
            obj.selected = False

    def select(self, x):
        if x is not None:
            self.nodes[x + self.click_offset].selected = True

    def click_obj(self, x):
        self.deselect_all()
        self.select(x - self.click_offset)
        self.callback(x - self.click_offset)

    def click_obj_left(self, x):
        self.callback_left(x - self.click_offset)

    def __init__(self, objects, objects_size=(32, 32), callback=lambda x=None: None, callback_left=lambda x=None: None,
                 rect=(0, 0, 100, 100), with_columns=True, horizontal=False, extras=None):
        if extras is None:
            extras = [ICON_ERASER]
        self.click_offset = len(extras)

        self.callback = callback
        self.callback_left = callback_left
        buttons = []
        for i in range(len(extras)):
            buttons.append(Button(0, 0, objects_size[0], objects_size[1],
                                  image_normal=extras[i].normal,
                                  image_down=extras[i].down,
                                  image_hover=extras[i].hover,
                                  callback=lambda x=i: self.click_obj(x),
                                  callback_left=lambda x=i: self.click_obj_left(x)))
        for i in range(len(objects)):
            buttons.append(Button(0, 0, objects_size[0], objects_size[1],
                                  image_normal=objects[i].image,
                                  image_down=objects[i].image_down,
                                  image_hover=objects[i].image_hover,
                                  callback=lambda x=i + self.click_offset: self.click_obj(x),
                                  callback_left=lambda x=i + self.click_offset: self.click_obj_left(x)))
        super(ObjectGuiContainer, self).__init__(buttons, rect, with_columns, horizontal)
